function getNextMondayTbill(sDate) {
    var today = new Date(sDate);
    if (sDate == "" || sDate == undefined) {
        today = new Date();
    }
//var today = new Date();
    var pMnth = "";
    var month = new Array(12);
    month[0] = "Jan";
    month[1] = "Feb";
    month[2] = "Mar";
    month[3] = "Apr";
    month[4] = "May";
    month[5] = "Jun";
    month[6] = "Jul";
    month[7] = "Aug";
    month[8] = "Sep";
    month[9] = "Oct";
    month[10] = "Nov";
    month[11] = "Dec";
    var diff = today.getDate() + (1 + 7 - today.getDay()) % 7;
    today.setDate(diff);
    var pday_TD = today.getDate();
    var pmonth_TD = today.getMonth() + 1;
    var pyear_TD = today.getFullYear();
    for (i = 0; i < 12; i++) {
        if (pmonth_TD == i + 1) {
            pMnth = month[i];
            break;
        }
    }

    return pday_TD + "-" + pMnth + "-" + pyear_TD;
}

